import React, { Fragment } from "react";
import { Box, Text } from "@chakra-ui/react";
const container = ({title}) => {
  return (
    <Fragment>

        <Box

        bgColor="gray.300" >

      <Box mt="25px">
        <Text
          color="#fff"
          textAlign="center"
          fontSize={{ base: "16px", md: "40px" }}
          fontWeight={700}
        >
          {title}
        </Text>
      </Box>
        </Box>
    </Fragment>
  );
};

export default container;
