import React from "react";
import { useDrag } from "react-dnd";
import { Box } from "@chakra-ui/react";
import Card from "./Card";

const DraggableCard = ({ card, index, title, handleDrop }) => {
  const [, ref] = useDrag({
    type: "CARD",
    item: { card, index }
  });

  const handleDragStart = (e) => {
    // Define the data being dragged (the card data)
    e.dataTransfer.setData("card", JSON.stringify(card));
  };

  return (
    <Box
      ref={(element) => {
        ref(element);
        element?.addEventListener("dragstart", handleDragStart); // Add dragstart event listener
      }}
    >
      {/* Render your Card component here with the provided card data */}
      <Card
        task={card.task}
        description={card.description}
        label={card.label}
      />
    </Box>
  );
};

export default DraggableCard;
