// DraggableCard.js
import React from "react";
import { useDrag } from "react-dnd";
import { Box } from "@chakra-ui/react";
import Card from "..";

const DraggableCard = ({ card, index }) => {
  const [, ref] = useDrag({
    type: "CARD",
    item: { card, index },
  });

  return (
    <Box ref={ref}>
      {/* Render your Card component here with the provided card data */}
      <Card task={card.task} description={card.description} label={card.label} />
    </Box>
  );
};

export default DraggableCard;
