import React, { Fragment } from "react";
import { Box, Text } from "@chakra-ui/react";
const container = (title) => {
  return (
    <Fragment>
      <Box mt="25px">
        <Text
          color="#000"
          textAlign="center"
          fontSize={{ base: "24px", md: "40px" }}
          fontWeight={700}
        >
          To-Do
        </Text>
      </Box>
    </Fragment>
  );
};

export default container;
