import React, { useState } from "react";
import { Flex } from "@chakra-ui/react";
import TaskSection from "../components/TaskSection"; // Import the TaskSection component

const Container = () => {
  const [cards, setCards] = useState([]);

  const handleDrop = (sectionKey, droppedCard) => {
    // Determine the previous section of the dropped card
    const previousSection = droppedCard.card.section;

    // Remove the card from its previous section
    const updatedCards = cards.filter((card) => card !== droppedCard.card);

    // Update the section of the dropped card
    droppedCard.card.section = sectionKey;

    // Add the card to the new section
    updatedCards.push(droppedCard.card);

    // Update the state with the new card arrangement
    setCards(updatedCards);
  };


  return (
    <Flex justifyContent="space-between">
      {/* Render "To Do" section */}
      <TaskSection
        title="To Do"
        sectionKey="todo"
        cards={cards.filter((card) => card.section === "todo")}
        onDrop={handleDrop}
      />

      {/* Render "In Progress" section */}
      <TaskSection
        title="In Progress"
        sectionKey="inprogress"
        cards={cards.filter((card) => card.section === "inprogress")}
        onDrop={handleDrop}
      />

      {/* Render "Completed" section */}
      <TaskSection
        title="Completed"
        sectionKey="completed"
        cards={cards.filter((card) => card.section === "completed")}
        onDrop={handleDrop}
      />
    </Flex>
  );
};

export default Container;
